SELECT hm.message_id AS id, hts.message_name, hm.last_update_date, hmt.trx_status_code AS statusCode, hmt.transaction_message AS failedReason, hmt.transaction_id AS transactionId, hm.creation_date AS creationDate FROM hmsg_message hm JOIN hmsg_message_transaction hmt ON hm.message_id = hmt.message_id
AND hmt.tenant_id = #{tenantId}
straight_join hmsg_template_server_line htsl ON htsl.template_code = hm.template_code JOIN hmsg_template_server hts ON hts.temp_server_id = htsl.temp_server_id WHERE hm.message_type_code = #{templateType}
and hmt.trx_status_code = #{status}
and hts.message_name LIKE concat(concat('%',#{subject}),'%')
AND ( hmt.trx_status_code LIKE concat(concat('%',#{params}),'%') OR hmt.transaction_message LIKE concat(concat('%',#{params}),'%') OR hts.message_name LIKE concat(concat('%',#{params}),'%') )
ORDER BY hm.message_id DESC

SELECT count(0) FROM ( SELECT hm.message_id AS id FROM hmsg_message hm
JOIN hmsg_message_transaction hmt ON hm.message_id = hmt.message_id JOIN hmsg_template_server_line htsl ON htsl.template_code = hm.template_code JOIN hmsg_template_server hts ON hts.temp_server_id = htsl.temp_server_id
WHERE hm.message_type_code = #{templateType}
and hmt.trx_status_code = #{status}
AND hmt.tenant_id = #{tenantId}
and hts.message_name LIKE concat(concat('%',#{subject}),'%')
AND ( hmt.trx_status_code LIKE concat(concat('%',#{params}),'%') OR hmt.transaction_message LIKE concat(concat('%',#{params}),'%') OR hts.message_name LIKE concat(concat('%',#{params}),'%') )
) tmp_count

SELECT hmt.trx_status_code, hmr.receiver_address , hm.message_id FROM hmsg_message hm INNER JOIN hmsg_message_receiver hmr ON hm.message_id = hmr.message_id INNER JOIN hmsg_message_transaction hmt ON hmt.message_id=hm.message_id WHERE hmr.receiver_address IN
#{email,jdbcType=VARCHAR}
AND hm.template_code = #{templateCode} AND hm.message_type_code="email" ORDER BY hm.message_id DESC
