mapper.select:
SELECT nms.ID, nms.ENV_ID, nms.NOTIFY_TYPE, nms.CODE, nms.PROJECT_ID, nms.EVENT_NAME, nms.SMS_ENABLE, nms.PM_ENABLE, nms.DT_ENABLE, nms.EMAIL_ENABLE, nms.OBJECT_VERSION_NUMBER, hts.message_name AS name, hts.SUBCATEGORY_CODE, nmstu.ID AS NMSTU_ID, nmstu.USER_ID, nmstu.TYPE, nmstu.MESSAGE_SETTING_ID FROM notify_message_setting nms INNER JOIN hmsg_template_server hts ON nms.CODE = hts.message_code LEFT JOIN notify_message_setting_target nmstu ON nmstu.MESSAGE_SETTING_ID = nms.ID WHERE nms.NOTIFY_TYPE = #{notifyType}
AND nms.PROJECT_ID = #{projectId}
and nms.code = #{code}
AND hts.ENABLED_FLAG = 1

SELECT
nms.*
FROM
notify_message_setting nms
WHERE
nms. CODE = #{messageCode}


        AND nms.sourceId = #{source_id}


        AND nms.sourceId = 0




        AND nms.sourceLevel = #{source_level}


        AND nms.sourceLevel = 0



    AND nms.ENV_ID = #{envId}


    AND nms.EVENT_NAME = #{eventName}
