mapper.select:
SELECT hts.temp_server_id AS id, hts.message_code, hts.message_name AS messageName, hts.description, hts.enabled_flag AS enabled, hts.subcategory_code AS message_type, hts.category_code AS LEVEL, hts.receive_config_flag, hts.object_version_number, nmst.edit FROM hmsg_template_server hts LEFT JOIN notify_message_setting_config nmst ON hts.message_code = nmst.message_code AND hts.tenant_id = nmst.TENANT_ID WHERE 1 = 1 AND hts.category_code IS NOT NULL AND hts.message_code IS NOT NULL
AND hts.category_code = #{level}
AND hts.message_code = #{categoryCode}
AND hts.message_name LIKE concat(concat('%',#{messageName}),'%')
AND hts.subcategory_code LIKE concat(concat('%',#{messageType}),'%')
AND hts.enabled_flag = #{enabled}
AND hts.receive_config_flag = #{allowConfig}
AND hts.message_code LIKE concat(concat('%',#{params}),'%')

SELECT hts.temp_server_id, hts.subcategory_code, hts.message_code, hts.message_name, hts.description FROM hmsg_template_server hts JOIN hmsg_template_server_line htsl ON htsl.temp_server_id = hts.temp_server_id AND htsl.enabled_flag = TRUE AND htsl.type_code = 'WEB_HOOK' AND htsl.template_code LIKE concat(concat('%',#{messageType}),'%') WHERE hts.category_code=#{level}
And hts.subcategory_code in
#{category}
And hts.subcategory_code not in
#{category}
And hts.message_name LIKE concat(concat('%',#{name}),'%')
