mapper.select:
SELECT
*
FROM
notify_webhook
where project_id=#{projectId}

    AND name LIKE concat(concat('%',#{filterDTO.name}),'%')


    AND type=#{filterDTO.type}


    AND enable_flag=#{filterDTO.enableFlag}


    AND (
    name LIKE concat(concat('%',#{params}),'%') OR
    webhook_path LIKE concat(concat('%',#{params}),'%')
    )
